<?php global $salong; ?>
<script type="text/javascript">var ias=$.ias({container:".ajaxposts,ul.products",item:".ajaxpost,.ajaxproduct",pagination:".navigation",next:".navigation a.next",});ias.extension(new IASSpinnerExtension());ias.extension(new IASTriggerExtension({text:'<?php echo $salong['loadmore_text']; ?>',offset:<?php echo $salong['loadmore_count'];?>,}));ias.extension(new IASNoneLeftExtension({text:'<?php echo $salong['loadmore_end']; ?>',}));<?php if ($salong['switch_lazyload']) { ?>ias.on('rendered',function(items){$(".container img,.content img,#content img").lazyload({effect:"fadeIn",failure_limit:10});})<?php } ?></script>